function activitySelection(start, finish) {
    const activities = start.map((s, i) => ({ start: s, finish: finish[i] }));
    activities.sort((a, b) => a.finish - b.finish);

    const result = [];
    let lastFinish = -1;

    for (let act of activities) {
        if (act.start >= lastFinish) {
            result.push(act);
            lastFinish = act.finish;
        }
    }

    return result;
}

