#include <iostream>
#include <vector>
#include <stack>

std::vector<std::vector<int>> adj;
std::vector<bool> visited;

// DFS Rekursif
void dfsRecursive(int u) {
    visited[u] = true;
    std::cout << u << " ";

    for (int v : adj[u]) {
        if (!visited[v]) dfsRecursive(v);
    }
}

// DFS Iteratif
void dfsIterative(int startNode) {
    std::vector<bool> localVisited(adj.size(), false);
    std::stack<int> s;

    s.push(startNode);
    localVisited[startNode] = true;

    while (!s.empty()) {
        int u = s.top();
        s.pop();

        std::cout << u << " ";

        for (int i = adj[u].size() - 1; i >= 0; --i) {
            int v = adj[u][i];
            if (!localVisited[v]) {
                localVisited[v] = true;
                s.push(v);
            }
        }
    }
}

