## Implementasi Algoritma Kahn (C++)

```cpp
#include <iostream>
#include <vector>
#include <queue>

std::vector<int> kahnTopologicalSort(int numNodes, const std::vector<std::vector<int>>& adj, std::vector<int>& inDegree) {
    std::queue<int> q;
    std::vector<int> result;

    for (int i = 0; i < numNodes; ++i) {
        if (inDegree[i] == 0) q.push(i);
    }

    int visitedNodes = 0;
    while (!q.empty()) {
        int u = q.front(); q.pop();
        result.push_back(u);
        visitedNodes++;

        for (int v : adj[u]) {
            if (--inDegree[v] == 0) q.push(v);
        }
    }

    if (visitedNodes != numNodes) {
        std::cout << "Grafik mengandung siklus!\n";
        return {};
    }

    return result;
}

int main() {
    int numNodes = 5;
    std::vector<std::vector<int>> adj(numNodes);
    std::vector<int> inDegree(numNodes, 0);

    adj[0] = {1, 2}; inDegree[1]++; inDegree[2]++;
    adj[1] = {3}; inDegree[3]++;
    adj[2] = {3}; inDegree[3]++;
    adj[3] = {4}; inDegree[4]++;

    auto order = kahnTopologicalSort(numNodes, adj, inDegree);
    for (int n : order) std::cout << n << " ";
    std::cout << "\n";
}

function solveNQueens(n) {
    const solutions = [];

    function isSafe(board, row, col) {
        for (let i = 0; i < col; i++) {
            if (board[row][i] === 1) return false;
        }
        for (let i = row, j = col; i >= 0 && j >= 0; i--, j--) {
            if (board[i][j] === 1) return false;
        }
        for (let i = row, j = col; i < n && j >= 0; i++, j--) {
            if (board[i][j] === 1) return false;
        }
        return true;
    }

    function solveUtil(board, col) {
        if (col === n) {
            solutions.push(board.map(row => [...row]));
            return;
        }
        for (let i = 0; i < n; i++) {
            if (isSafe(board, i, col)) {
                board[i][col] = 1;
                solveUtil(board, col + 1);
                board[i][col] = 0;
            }
        }
    }

    const board = Array(n).fill().map(() => Array(n).fill(0));
    solveUtil(board, 0);
    return solutions;
}

